class DfdHeaderBuilder_DefaultPresets {

	/**
	 *
	 * @var DfdHeaderBuilder_DefaultPresets $_instance 
	 */
	private static $_instance = null;

	public function getAll() {

		$def_presets = array (
						DfdHeaderBuilder_DefaultPresetsValues::getPresetHeader1(),
						DfdHeaderBuilder_DefaultPresetsValues::getHeader2(),
						DfdHeaderBuilder_DefaultPresetsValues::getHeader3(),
						DfdHeaderBuilder_DefaultPresetsValues::getHeader4(),
						DfdHeaderBuilder_DefaultPresetsValues::getHeader5(),
						DfdHeaderBuilder_DefaultPresetsValues::getHeader6(),
						DfdHeaderBuilder_DefaultPresetsValues::getHeader7(),
//						DfdHeaderBuilder_DefaultPresetsValues::getHeader8(),
		);
		$def_presets = $this->createStdObj($def_presets);
		return $def_presets;
	}

	protected function createStdObj($presets) {
		$result = array ();
		foreach ($presets as $key => $preset) {
			if(is_string($preset)){
				$decoded = json_decode($preset);
				array_push($result, $decoded);
			}
		}
		return $result;
	}

	public static function instance() {
		if (is_null(self::$_instance)) {
			self::$_instance = new self();
		}
		return self::$_instance;
	}

	function __construct() {
		
	}

}
